     Thread t1 = new Thread(r);
     t1.setName("小明");
     setInterrupt(true,"小刚");
     Thread t2 = new Thread(r);
     t2.setName("小刚");
     setInterrupt(true,"小明");
     t1.start();
     t2.start();
 }
}


